library(shiny)

# Define UI
ui <- fluidPage(
  titlePanel("Durchschnittsberechnung für numerische Variablen"),
  sidebarLayout(
    sidebarPanel(
      fileInput("file1", "Wählen Sie eine RData-Datei:", accept = ".rdat")
    ),
    mainPanel(
      tableOutput("meanTable")
    )
  )
)

# Define server logic
server <- function(input, output) {
  output$meanTable <- renderTable({
    inFile <- input$file1
    
    if (is.null(inFile))
      return(NULL)
    
    data <- readRDS(inFile$datapath)  # Laden der .rdat Datei
    
    # Berechnung des Mittelwerts für jede numerische Spalte
    if (is.data.frame(data)) {
      numericData <- data[sapply(data, is.numeric)]  # Auswahl der numerischen Daten
      means <- colMeans(numericData, na.rm = TRUE)   # Mittelwertberechnung, NA-Werte ignorieren
      return(data.frame(Variable = names(means), Mean = means))
    } else {
      return(data.frame(Variable = NA, Mean = NA))
    }
  })
}

# Run the application 
shinyApp(ui = ui, server = server)
